<?phpfunction isPrime($num) {    if ($num < 2) return false;    for ($i = 2; $i <= sqrt($num); $i++) {        if ($num % $i == 0) return false;    }    return true;}if ($_SERVER["REQUEST_METHOD"] == "POST") {    $n = intval($_POST["numero"]);    $count = 0;    $num = 2;    $primes = [];    while ($count < $n) {        if (isPrime($num)) {            $primes[] = $num;            $count++;        }        $num++;    }    echo "<h1>I primi $n numeri primi sono:</h1>";    echo implode(", ", $primes);}?>